  mutate(air_time2 = as.numeric(difftime(arr_dt, dep_dt)))

fp &lt;- flights_airtm |&gt; 
  sample_n(3000) |&gt;
  ggplot(aes(x=air_time, y=air_time2, label = paste(origin, dest))) + 
